import React, { useState, useEffect } from 'react';

const WaterTracker = () => {
  const [waterIntake, setWaterIntake] = useState(0);
  const [droplets, setDroplets] = useState([]);
  const dailyGoal = 2000;

  useEffect(() => {
    const percentage = Math.min((waterIntake / dailyGoal) * 100, 100);
    const progressElement = document.querySelector(".circular-progress");
    const valueElement = document.querySelector(".progress-value");
    
    if (progressElement && valueElement) {
      progressElement.style.setProperty("--progress", `${(percentage / 100) * 360}deg`);
      valueElement.textContent = `${Math.round(percentage)}%`;
    }
  }, [waterIntake]);

  const createDroplet = () => {
    const id = Date.now();
    setDroplets(prev => [...prev, id]);

    // Remove droplet after animation completes
    setTimeout(() => {
      setDroplets(prev => prev.filter(dropletId => dropletId !== id));
    }, 1000);
  };

  const addWaterIntake = () => {
    if (waterIntake < dailyGoal) {
      const newIntake = waterIntake + 200;
      setWaterIntake(newIntake > dailyGoal ? dailyGoal : newIntake);
      createDroplet();
    }
  };

  return (
    <div className="relative p-4 bg-white rounded-lg shadow">
      <h2 className="text-xl font-semibold mb-4">Water Intake</h2>
      
      <div className="relative h-48">
        {droplets.map(id => (
          <div
            key={id}
            className="absolute left-1/2 -translate-x-1/2 top-0 
                     w-4 h-4 rounded-full bg-blue-400 opacity-80"
            style={{
              animation: 'droplet 1s ease-in forwards'
            }}
          />
        ))}
        
        <div className="circular-progress">
          <span className="progress-value">0%</span>
        </div>
      </div>

      <button
        onClick={addWaterIntake}
        className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-lg
                   hover:bg-blue-600 transition-colors
                   disabled:opacity-50 disabled:cursor-not-allowed"
        disabled={waterIntake >= dailyGoal}
      >
        Add 200ml
      </button>

      <style>{`
        @keyframes droplet {
          0% {
            transform: translateX(-50%) translateY(0) scale(1);
            opacity: 0.8;
          }
          50% {
            transform: translateX(-50%) translateY(60px) scale(0.8);
            opacity: 0.6;
          }
          100% {
            transform: translateX(-50%) translateY(120px) scale(0.5);
            opacity: 0;
          }
        }

        .circular-progress {
          position: relative;
          width: 120px;
          height: 120px;
          border-radius: 50%;
          background: conic-gradient(#00f0ff var(--progress), #f3f3f3 0);
          display: flex;
          align-items: center;
          justify-content: center;
          margin: 20px auto;
          --progress: 0deg;
        }

        .progress-value {
          position: absolute;
          font-size: 1.5em;
          color: #0073e6;
        }
      `}</style>
    </div>
  );
};
export default Main;
