import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './login.css';  

function Signup() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loc, setLoc] = useState(null);
  const age = null;
  const height = null;
  const weight = null;
  const navigate = useNavigate();

  useEffect(() => {
    document.body.style.overflow = 'hidden';

    // Get user's location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setLoc(`${latitude}, ${longitude}`); // Store as a string or format as needed
        },
        (error) => {
          console.error('Error getting location:', error);
          setLoc('Location not available'); // Default location or handle error
        }
      );
    } else {
      setLoc('Geolocation not supported'); // Handle case where geolocation is not supported
    }

    return () => {
      document.body.style.overflow = '';
    };
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/signup', {
        method: 'POST', // Changed to POST for saving data
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password, loc, age, height, weight }), // Include all signup details
      });

      const data = await response.json();

      if (response.ok) {
        // Signup successful, notify user and redirect to login page or main page
        alert('Signup successful!');
        navigate('/main');
      } else {
        alert(data.message || 'Signup failed. Please try again.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred. Please try again.');
    }
  };

  return (
    <div id="login_page">
      <div id="image-container">
        <img id="_1_img" src={`${process.env.PUBLIC_URL}/img3.jpg`} alt="Fitness Image" />
        <div id="overlay"></div>
      </div>

      <div id="text-overlay">
        <h1>Fitness Logger</h1>
      </div>

      <div id="login">
        <h2>Sign up</h2><br />
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Enter Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="email"
            placeholder="Enter Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Enter Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <br/><br/>
          <button type="submit">Sign Up</button>
        </form>
        <br />
      </div>
    </div>
  );
}

export default Signup;
