import React, { useState, useEffect } from 'react';
import './profile.css'; 

const UserProfile = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [profile, setProfile] = useState({
    name: '',
    email: '',
    loc: '',
    age: null,
    height: null,
    weight: null,
    img: '',
  });

  useEffect(() => {
    setProfile({
      name: localStorage.getItem('username'),
      email: localStorage.getItem('email'),
      loc: localStorage.getItem('loc'),
      age: localStorage.getItem('age'),
      height: localStorage.getItem('height'),
      weight: localStorage.getItem('weight'),
      img: localStorage.getItem('img'),
    });
  }, []);

  const toggleEditMode = () => {
    setIsEditing((prev) => !prev);
  };

  const handleInputChange = (event) => {
    const { id, value } = event.target;
    setProfile((prevProfile) => ({
      ...prevProfile,
      [id]: value,
    }));
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = function(e) {
      setProfile((prevProfile) => ({
        ...prevProfile,
        img: e.target.result, // Update img instead of profilePicture
      }));
    }
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://localhost:5000/update', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: profile.name,
          loc: profile.loc,
          age: profile.age,
          height: profile.height,
          weight: profile.weight,
          img: profile.img,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        alert(data.message || 'Profile updated successfully!');
        setIsEditing(false);
        // Update localStorage with new profile data
        localStorage.setItem('loc', profile.loc);
        localStorage.setItem('age', profile.age);
        localStorage.setItem('height', profile.height);
        localStorage.setItem('weight', profile.weight);
        localStorage.setItem('img', profile.img);
      } else {
        alert(data.message || 'Failed to update profile. Please try again.');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('An error occurred while updating the profile.');
    }
  };

  const cancelEdit = () => {
    setIsEditing(false);
  };

  return (
    <div>
      <div id="topbar">
        <h1 id="title_company">Fitness Logger</h1>
        <h2 id="about-us"><a href="/about_us">About us</a></h2>
      </div>

      <div id="profile-section">
        <h2 style={{ position: 'relative', left: '60px' }}>Profile</h2>
        <div id="profile-content">
          <img src={profile.img} alt="Profile" id="profile-photo" />
          <div id="user-details">
            {isEditing ? (
              <div id="edit-mode">
                <div className="input-group">
                  <label htmlFor="profile-picture">Profile Picture:</label>
                  <input
                    type="file"
                    id="profile-picture"
                    accept="image/*"
                    onChange={handleFileChange}
                  />
                </div>
                <div className="input-group">
                  <label htmlFor="name">Name:</label>
                  <input
                    type="text"
                    id="name"
                    value={profile.name}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="input-group">
                  <label htmlFor="loc">Location:</label>
                  <input
                    type="text"
                    id="loc" // Use loc instead of location
                    value={profile.loc}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="input-group">
                  <label htmlFor="age">Age:</label>
                  <input
                    type="number"
                    id="age"
                    value={profile.age}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="input-group">
                  <label htmlFor="height">Height (cm):</label>
                  <input
                    type="number"
                    id="height"
                    value={profile.height}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="input-group">
                  <label htmlFor="weight">Weight (kg):</label>
                  <input
                    type="number"
                    id="weight"
                    value={profile.weight}
                    onChange={handleInputChange}
                  />
                </div>
                <button className="edit-button" type="button" onClick={handleSubmit}>
                  Submit
                </button>
                <button className="edit-button" type="button" onClick={cancelEdit}>
                  Cancel
                </button>
              </div>
            ) : (
              <div id="view-mode">
                <h3>{profile.name}</h3>
                <p><strong>Email:</strong> {profile.email}</p>
                <p><strong>Location:</strong> {profile.loc}</p>
                <p><strong>Age:</strong> {profile.age}</p>
                <p><strong>Height:</strong> {profile.height}</p>
                <p><strong>Weight:</strong> {profile.weight}</p>
                <button className="edit-button" onClick={toggleEditMode}>Edit Profile</button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
